import React, { useState } from "react";
import axios from "axios";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/login", {
        email,
        password,
      });
      alert("Login successful!");
      localStorage.setItem("token", res.data.token);
    } catch (err) {
      alert("Login failed: " + err.response.data.message);
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-blue-950 text-white">
      <div className="bg-black bg-opacity-50 p-8 rounded-lg shadow-lg w-96 text-center">
        <h1 className="text-3xl font-bold mb-2">AVANYA AI</h1>
        <p className="text-sm mb-6">Empowering Education with Intelligence</p>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="mb-4 w-full p-2 rounded"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="mb-4 w-full p-2 rounded"
        />
        <button
          onClick={handleLogin}
          className="bg-blue-600 hover:bg-blue-700 w-full p-2 rounded"
        >
          Login
        </button>
      </div>
    </div>
  );
};

export default Login;
